import React, { useState, useEffect } from 'react';
import Card from './Card'; // Assuming you have a Card component for displaying each item
import axios from 'axios'; 
import "./Display.css"

const DisplayCard = () => {
  const [scienceFictionItems, setScienceFictionItems] = useState([]);

  useEffect(() => {
    axios.get('https://child.onrender.com/api/sciencefiction')
      .then(response => {
        setScienceFictionItems(response.data.slice(0, 8)); 
      })
      .catch(error => {
        console.error('Error fetching data:', error);
      });
  }, []);

  return (
    <div id='DisplayMain'>
      {scienceFictionItems.map(item => (
        <Card key={item.id} Title={Title} />
      ))}
    </div>
  );
}

export default DisplayCard;
