import React, { useState, useEffect } from "react";
import Card from "./Card";
import axios from "axios";
import "./Display.css";
import { IconButton } from "@mui/material";
import ArrowBackIcon from "@mui/icons-material/ArrowBack";
import ArrowForwardIcon from "@mui/icons-material/ArrowForward";
const DisplayCard = () => {
  const [scienceFictionItems, setScienceFictionItems] = useState([]);

  useEffect(() => {
    axios
      .get("https://child.onrender.com/api/sciencefiction")
      .then((response) => {
        // Assuming the response data is an array of objects with image URLs under the "Image" property
        setScienceFictionItems(response.data.slice(0, 8));
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
      });
  }, []);

  const handleNext = () => {};

  return (
    <>
      <div id="DisplayMain">
        {scienceFictionItems.map((item) => (
          <Card
            key={item._id}
            title={item.Title}
            // image={item.Image && item.Image[0]}
            status={item.Status}
            data={item}
          />
        ))}
      </div>
      <div id="PreviousNext">
        <IconButton onClick={handleNext}>
            <ArrowBackIcon />
        </IconButton>
        <IconButton onClick={handleNext}>
          Next <ArrowForwardIcon />
        </IconButton>
      </div>
    </>
  );
};

export default DisplayCard;
