import React, { useState, useEffect } from "react";
import Card from "./Card";
import axios from "axios";
import "./Display.css";
import { IconButton } from "@mui/material";
import ArrowBackIcon from "@mui/icons-material/ArrowBack";
import ArrowForwardIcon from "@mui/icons-material/ArrowForward";
const DisplayCard = () => {
  const [scienceFictionItems, setScienceFictionItems] = useState([]);

  useEffect(() => {
    axios
      .get("https://child.onrender.com/api/sciencefiction")
      .then((response) => {
        setScienceFictionItems(response.data.slice(0, 8));
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
      });
  }, []);

  const handleNext = () => {};

  return (
    <>
      <div id="DisplayMain">
        {scienceFictionItems.map((item) => (
          <Card
            key={item._id}
            title={item.Title}
            status={item.Status}
            data={item}
          />
        ))}
      </div>
      <div id="PreviousNext">
        <IconButton>
          <ArrowBackIcon />
          <span style={{ color: "linear-gradient(to right, rgba(4, 70, 141, 0.5) 20%, #38c7ff 50%);" }}>Previous</span>
        </IconButton>
        <IconButton onClick={handleNext}>
          <span style={{ color: "white" }}>Next</span>
          <ArrowForwardIcon />
        </IconButton>
      </div>
    </>
  );
};

export default DisplayCard;
